var http = require('http');
var url = require("url");
var optimist = require('optimist');
var os = require('os');

var interfaces = os.networkInterfaces(),
    deviceNames = Object.keys(interfaces),
    deviceName,
    deviceCount = deviceNames.length;

global.networkInterfaces = {};

while (deviceCount) {
    deviceCount--;
    deviceName = deviceNames[deviceCount];

    interfaces[deviceName].forEach(function(info) {
        if (!info.internal && 'IPv4' === info.family) {
            global.networkInterfaces[deviceName] = info.address;
        }
    });
}

var defaultNetworkinterface = "en1";
//TODO handle when this network interface is not available

var argv = optimist
    .usage('Usage: $0 [-p 1337] [-n en1 | [--app-host=HOST] [--redirect-host=HOST]] [--redirect-path=carconfigurator] [--app-port=8082]')
    .describe('p', 'The port this server will listen on')
    .default('p', '1337')
    .describe('n', 'The network interface to use to populate app-host and redirect-host automatically')
    .describe('app-host', 'The hostname from which to receive shared configurations')
    .describe('redirect-host', 'The hostname to which to redirect')
    .describe('redirect-path', 'The path at the redirect host to which to redirect')
    .default('redirect-path', 'carconfigurator')
    .describe('app-port', 'The port the application is running on both app and redirect hosts')
    .default('app-port', '8082')
    .boolean('help')
    .describe('help', 'Show usage')
    .check(function(args) {

        if (args.p) {
            if (parseInt(args.p, 10)) {
                port = args.p;
            } else {
                throw new Error("Invalid port specified: " + args.p);
            }
        }

        if (args.n) {
            if (args["app-host"] || args["redirect-host"]) {
                throw new Error("Cannot specify both network interface and host names");
            }
        } else if ((args["app-host"] ||  args["redirect-host"]) && !(args["app-host"] && args["redirect-host"])) {
            throw new Error("Must provide both app-host and redirect-host if not using detected network interface");
        }

    })
    .argv;

if (argv.help) {
    console.log(optimist.help());
    process.exit();
}

global.lastHash = "";
global.appHost = argv['app-host'] ? argv['app-host'] : global.networkInterfaces[defaultNetworkinterface];
global.redirectHost = argv['redirect-host'] ? argv['redirect-host'] : global.networkInterfaces[defaultNetworkinterface];
global.redirectPath = argv['redirect-path'];
global.appPort = argv['app-port'];

http.createServer(function (req, res) {

    if ("POST" === req.method) {
        res.writeHead(200, {
            'Content-Type': 'text/plain',
            'Access-Control-Allow-Origin': 'http://' + global.appHost + ':' + global.appPort
        });

        var query = url.parse(req.url).query
        global.lastHash = query.replace("hash=%23!", "");
        console.log("Save <- " + decodeURIComponent(global.lastHash));

    } else {
        var location = 'http://' + global.redirectHost + ":" + global.appPort + "/" + global.redirectPath + '/#!' + global.lastHash;
        console.log("Redirect -> " + location);

        res.writeHead(302, {
            'Content-Type': 'text/plain',
            'Location': location
        });
    }

    res.end();

}).listen(port);

//console.log('Accepting shared configurations from: http://' + global.appHost + ':' + global.appPort);
console.log('1) Open this, configure, and share: http://' + global.redirectHost + ':' + global.appPort + "/" + global.redirectPath);
console.log('2) Open this to load shared config: http://' + global.appHost + ':' + global.port);
